  async getVehicles(tenantId: string, pagination: PaginationParams) {
    const vehicles = await this.vehiclesRepository.findByTenant(tenantId, pagination)
    const count = await this.vehiclesRepository.countByTenant(tenantId)

    return {
      data: vehicles.map(this.transformVehicle),
      pagination: this.buildPaginationMeta(pagination, count)
    }
  }
