// repositories/vehicles.repository.ts
class VehiclesRepository {
  constructor(private db: Pool) {}

  async findByTenant(tenantId: string, pagination: PaginationParams) {
    const offset = (pagination.page - 1) * pagination.limit
    const result = await this.db.query(
      `SELECT * FROM vehicles WHERE tenant_id = $1 ORDER BY created_at DESC LIMIT $2 OFFSET $3`,
      [tenantId, pagination.limit, offset]
    )
    return result.rows
  }
}

